errors.example:
/* ❌ Wrong */
int x = 5
main() { }

/* ✅ Correct */
int x = 5;
int main() { return 0; }

/* ❌ Wrong */
extern int foo();
int main() { foo(); } /* missing definition */

/* ✅ Correct */
int foo() { return 0; }
int main() { foo(); }

/* ❌ Wrong */
int arr[5];
arr[10] = 0;

/* ✅ Correct */
int arr[5];
if (index < 5) arr[index] = 0;

/* ❌ Wrong */
int x = 5.5;

/* ✅ Correct */
int x = (int)5.5;

/* ❌ Wrong */
int *p = NULL;
*p = 5;

/* ✅ Correct */
int *p = malloc(sizeof(int));
if (p != NULL) *p = 5;
free(p);

/* ❌ Wrong */
int *p = malloc(sizeof(int));
/* no free */

/* ✅ Correct */
int *p = malloc(sizeof(int));
*p = 5;
free(p);

/* ❌ Wrong */
int res = 10 / 0;

/* ✅ Correct */
if (denom != 0) res = num / denom; else res = 0;

/* ❌ Wrong */
int x;
printf("%d", x);

/* ✅ Correct */
int x = 0;
printf("%d", x);

/* ❌ Wrong */
int x = INT_MAX; x++;

/* ✅ Correct */
unsigned int x = UINT_MAX; // Handle wraparound
