// ❌ Wrong
Promise.any([Promise.reject('err1'), Promise.reject('err2')])

// ✅ Correct
Promise.allSettled([promise1, promise2]).then(results => {
  results.forEach((result, index) => {
    if (result.status === 'rejected') {
      console.error(`Error ${index}:`, result.reason);
    }
  });
});

