errors.example:
// Wrong
String name = null;
System.out.println(name.length());

// Correct
if (name != null) {
  System.out.println(name.length());
}

// Wrong
int[] nums = {1, 2, 3};
System.out.println(nums[3]);

// Correct
if (idx >= 0 && idx < nums.length) {
  System.out.println(nums[idx]);
}

// Wrong
int n = Integer.parseInt("abc");

// Correct
if (str.matches("\\d+")) {
  int n = Integer.parseInt(str);
}
