errors.example:
// ❌ Wrong
let str: string = 5;

// ✅ Correct
let num: number = 5;
let str: string = num.toString();

// ❌ Wrong
console.log(undefinedVar);

// ✅ Correct
declare const myVar: string = 'hello';
console.log(myVar);

// ❌ Wrong
import 'missing-module';

// ✅ Correct
npm install missing-module @types/missing-module

// ❌ Wrong
interface Person { name: string; age: number; }
const p: Person = { name: 'John' };

// ✅ Correct
interface Person { name: string; age?: number; }
const p: Person = { name: 'John' };

// ❌ Wrong
function id<T extends string>(arg: T): T { return arg; }
id<number>(5);

// ✅ Correct
function id<T extends string>(arg: T): T { return arg; }
id<string>('hello');

// ❌ Wrong
type StrNum = string | number;
let val: StrNum = true;

// ✅ Correct
type StrNumBool = string | number | boolean;
let val: StrNumBool = true;

// ❌ Wrong
async function getData(): string {
  return fetch('/api').then(r => r.json());
}

// ✅ Correct
async function getData(): Promise<string> {
  const r = await fetch('/api');
  return r.text();
}

// ❌ Wrong
let x: number = 'string';

// ✅ Correct
let x: number = 5;

// ❌ Wrong
let obj: {prop: string} | null = null;
console.log(obj.prop);

// ✅ Correct
let obj: {prop: string} | null = null;
console.log(obj?.prop);

// ❌ Wrong
export default function fn() {}
// import fn;

// ✅ Correct
import fn from './module';
